//Last Updated: Ben Ray 1/13/22
import React, { useState, useEffect, useRef } from "react";
import {Chart} from 'react-chartjs-2';
const QuarterbackStats = ({ quarterback, qbStats }) => {
  const chart = useRef();

  useEffect(() =>{
    chart = new Chart(canvas, {
      type: 'line',
      data: {
        labels: ['1', '2', '3', '4', '5'],
        datasets: [{
          label: 'A',
          yAxisID: 'A',
          data: [100, 96, 84, 76, 69]
        }, {
          label: 'B',
          yAxisID: 'B',
          data: [1, 1, 1, 1, 0]
        }]
      },
      options: {
        scales: {
          yAxes: [{
            id: 'A',
            type: 'linear',
            position: 'left',
          }, {
            id: 'B',
            type: 'linear',
            position: 'right',
            ticks: {
              max: 1,
              min: 0
            }
          }]
        }
      }
    });

  })

  return (
    <>
      <div className="quarterback-stats-wrapper">
        <div className="quarterback">
          {quarterback.fullName}
        </div>
        <div className="stats">
          Stats
          <canvas ref={chart}></canvas>
          {qbStats && qbStats.map((game, i) => 
            <div key={i}>{game.week}</div>
          )}
        </div>
      </div>
    </>
  );
};

export default QuarterbackStats;
