//Last Updated: Ben Ray 1/18/22
import React, { Component } from "react";

import '../styles/quarterback.scss'
import QuarterbacksList from "./QuarterbacksList";
import playerServices from "../services/playerServices";

class Quarterbacks extends Component {
  constructor(props) {
    super(props);
    this.state = {
      players: [],
      quarterbacks: [],
      selectedQb: 0,
      stats: []
    };
  }

  componentDidMount = () => {
    playerServices
        .fetchPlayers()
        .then((res) => res.json())
        .then((result) => {
        return result;
      })
      .then(
        (result) => {
         console.log(result);
        this.setState({
          quarterbacks: result
        });
      },

         (error) => {}
    );
  };

  _selectQuarterback = (playerId) => {
    this.setState({
      selectedQb: playerId
    })

    // get stats
    playerServices
        .fetchPlayer(playerId)
        .then((res) => res.json())
        .then((result) => {
        return result;
      })
      .then(
        (result) => {
         console.log(result);
        // this.setState({
        //   quarterbacks: result
        // });
      },

         (error) => {}
    );
  }

  render = () => {
    const {
      quarterbacks,
      selectedQb
    } = this.state;
    return (
      <>
        <h1>Quarterbacks</h1>
        <div className="quarterback-wrapper">
            <div className="quarterback-view">
                <div className="quarterback-list">
                  <QuarterbacksList 
                    quarterbacks={quarterbacks}
                    selectPlayer={(playerId) => this._selectQuarterback(playerId)}
                    selectedQb={selectedQb}
                  />
                </div>
                <div className="quarterback-stats">
                    Stats
                </div>
            </div>
        </div>
      </>
    );
  };
}

export default Quarterbacks;
