//Last Updated: Ben Ray 1/18/22
import React, { Component, createRef } from "react";
import { connect } from "react-redux";
import Line from 'chart.js/auto';

const LineGraph= ({ quarterback, qbStats }) => {


 const ctx = document.getElementById('myChart').getContext('2d');
    this.setState({
      qbStats: this.props.qbStats,
      chart: new Line(ctx, {
        type: 'line',
        data: {
            labels: ['orange', 'gren', 'blue'],
            datasets: [{
                label: '# of Votes',
                data: [12, 19, 3, 5, 2, 3],
                backgroundColor: [
                    'rgba(255, 99, 132, 0.2)',
                    'rgba(54, 162, 235, 0.2)',
                    'rgba(255, 206, 86, 0.2)',
                    'rgba(75, 192, 192, 0.2)',
                    'rgba(153, 102, 255, 0.2)',
                    'rgba(255, 159, 64, 0.2)'
                ],
                borderColor: [
                    'rgba(255, 99, 132, 1)',
                    'rgba(54, 162, 235, 1)',
                    'rgba(255, 206, 86, 1)',
                    'rgba(75, 192, 192, 1)',
                    'rgba(153, 102, 255, 1)',
                    'rgba(255, 159, 64, 1)'
                ],
                borderWidth: 1
            }]
        },
        options: {
            scales: {
                y: {
                    beginAtZero: true
                }
            }
        }
    })
    })



  };


export default LineGraph;
