//Last Updated: Ben Ray 1/18/22
import React, { Component, createRef, useState, useEffect } from "react";
import { connect } from "react-redux";
import Chart from 'chart.js/auto';

let LineChart;

const LineGraph = ({ quarterback, qbStats }) => {
  const graphLabels = [];
  const labels = qbStats.forEach( (x) => {
    graphLabels.push(`${x.week}`);
  });

  console.log(graphLabels);

  useEffect(() => {
    buildChart();
  },[]);

  const buildChart = () => {
    var ctx=document.getElementById("LineChart").getContext("2d");

    if (typeof LineChart !== "undefined") LineChart.destroy();

    LineChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: graphLabels,
        datasets: [{
          label: 'dataset',
          data: graphLabels,
          fill: false,
          borderColor: 'rgb(100,100,100)',
          tension: 0.1
        }],
      },
    });
  }

  return (
    <>
    <div className="App">
    <h3>React Line Chart Example - CodeCheef </h3>
      <canvas id="LineChart" width="200" height="100"></canvas>
    </div>
    </>
  );

};











export default LineGraph;
