//Last Updated: Ben Ray 1/18/22
import React, { Component, createRef } from "react";
import { connect } from "react-redux";
import Line from 'chart.js/auto';

class LineGraph extends Component {
  constructor(props) {
    super(props);
    this.state = {
      qbStats: []
    };

    this.ctx = React.createRef();
  }

  componentWillUpdate = () => {
    this.setState({
      qbStats: this.props.qbStats
    })
  }


  componentDidMount = () => {
 console.log(this.state.qbStats);

    const ctx = document.getElementById('myChart').getContext('2d');
    const myChart = new Line(ctx, {
      type: 'line',
      data: {
          labels: ['Red', 'Blue', 'Yellow', 'Green', 'Purple', 'Orange'],
          datasets: [{
              label: '# of Votes',
              data: [12, 19, 3, 5, 2, 3],
              backgroundColor: [
                  'rgba(255, 99, 132, 0.2)',
                  'rgba(54, 162, 235, 0.2)',
                  'rgba(255, 206, 86, 0.2)',
                  'rgba(75, 192, 192, 0.2)',
                  'rgba(153, 102, 255, 0.2)',
                  'rgba(255, 159, 64, 0.2)'
              ],
              borderColor: [
                  'rgba(255, 99, 132, 1)',
                  'rgba(54, 162, 235, 1)',
                  'rgba(255, 206, 86, 1)',
                  'rgba(75, 192, 192, 1)',
                  'rgba(153, 102, 255, 1)',
                  'rgba(255, 159, 64, 1)'
              ],
              borderWidth: 1
          }]
      },
      options: {
          scales: {
              y: {
                  beginAtZero: true
              }
          }
      }
  });
  };



  render = () => {
    const { qbStats } = this.props;
    return (
      <>
        <h1>Chart</h1>
        {qbStats && qbStats.map((x, i) => <div key={i}>dddd</div>) }dddd
        <div>
        <canvas id="myChart" width="100" height="100"></canvas>
            </div>
      </>
    );
  };

}

const mapStateToProps = state => ({
  qbStats: state.qbStats,
});

export default connect(mapStateToProps)(LineGraph)
