//Last Updated: Ben Ray 1/13/22
import React, { Component } from "react";
import '../styles/quarterback.scss'
//import "../styles/teams.css";
import QuarterbacksList from "./QuarterbacksList";
import playerServices from "../services/playerServices";

// We want this component to go get the teams from MLB's Stats API
// the request is a GET to `https://statsapi.mlb.com/api/v1/teams?season=2021&sportId=1`
// From there we want to display each team and the members of each team
// To get the members of each team we can call the stats API with the team ID
// GET to `http://statsapi.mlb.com/api/v1/teams/{teamId}/roster?rosterType=active`

class Quarterbacks extends Component {
  constructor(props) {
    super(props);
    this.state = {
      players: [],
      quarterbacks: [],
      selectedQb: 0
    };
  }

  componentDidMount = () => {
    playerServices
        .fetchPlayers()
        .then((res) => res.json())
        .then((result) => {
        return result;
      })
      .then(
        (result) => {
         console.log(result);
        this.setState({
          quarterbacks: result
        });
      },

         (error) => {}
    );
  };

  _selectQuarterback = (playerId) => {
    console.log(playerId);
    this.setState({
      selectedQb: playerId
    })
  }

  render = () => {
    const {
      quarterbacks,
      selectedQb
    } = this.state;
    return (
      <>
        <p>Hello</p>
<h1>Quarterbacks</h1>
        <div className="quarterback-wrapper">
            <div className="quarterback-view">
                <div className="quarterback-list">
                  <QuarterbacksList 
                    quarterbacks={quarterbacks}
                    selectPlayer={(playerId) => this._selectQuarterback(playerId)}
                    selectedQb={selectedQb}
                  />
                </div>
                <div className="quarterback-stats">
                    Stats
                </div>
            </div>
        </div>
      </>
    );
  };




}

export default Quarterbacks;
